admst.admst:template:
#define NP(p) inst->LIMPRED_##p
#define NP(p) *(ckt->CKTrhsOld + inst->p##_Node)
#define BP(p,n) (NP(p) - NP(n))
#define BR(p,n) *(ckt->CKTrhsOld + inst->p##_##n##_Branch)
#define M(v) inst->m*(v)
#define M(v) (v)
#define lsr_1(p, v)\\ ckt->rhsadd(inst->p##_Node, -v);
#define lsr_2(p, n, v)\\ { double v_tmp_r = v;\\ ckt->rhsadd(inst->p##_Node, -v_tmp_r);\\ ckt->rhsadd(inst->n##_Node, v_tmp_r); }
#define _load_static_residual1(p, v)\\ lsr_1(p, M(v))
#define _load_static_residual2(p, n, v)\\ lsr_2(p, n, M(v))
#define _load_static_jacobian1(Sp, Pp, v)\\ { double v_tmp_ = M(v);\\ lsr_1(Sp, -v_tmp_*NP(Pp))\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, v_tmp_);\\ (inst->JSVAL_##Sp##_##Pp) += v_tmp_; }
#define _load_static_jacobian2s(Sp, Sn, Pp, v)\\ { double v_tmp_ = M(v);\\ lsr_2(Sp, Sn, -v_tmp_*NP(Pp))\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, v_tmp_);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pp, -v_tmp_);\\ (inst->JSVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JSVAL_##Sn##_##Pp) -= v_tmp_; }
#define _load_static_jacobian2p(Sp, Pp, Pn, v)\\ { double v_tmp_ = M(v);\\ lsr_1(Sp, -v_tmp_*BP(Pp, Pn))\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, v_tmp_);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pn, -v_tmp_);\\ (inst->JSVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JSVAL_##Sp##_##Pn) -= v_tmp_; }
#define _load_static_jacobian4(Sp, Sn, Pp, Pn, v)\\ { double v_tmp_ = M(v);\\ lsr_2(Sp, Sn, -v_tmp_*BP(Pp, Pn))\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, v_tmp_);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pn, v_tmp_);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pn, -v_tmp_);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pp, -v_tmp_);\\ (inst->JSVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JSVAL_##Sn##_##Pn) += v_tmp_;\\ (inst->JSVAL_##Sp##_##Pn) -= v_tmp_;\\ (inst->JSVAL_##Sn##_##Pp) -= v_tmp_; }
#define _load_branch_P(p, n)\\ ckt->ldset(inst->PTR_##p##_##n##_Branch_PB, 1.0);\\ ckt->ldset(inst->PTR_##p##_##n##_Branch_BP, 1.0);
#define _load_branch_N(p, n)\\ ckt->ldset(inst->PTR_##p##_##n##_Branch_NB, -1.0);\\ ckt->ldset(inst->PTR_##p##_##n##_Branch_BN, -1.0);
#define _load_branch_static_residual(p, n, v)\\ { double v_tmp_ = M(v);\\ ckt->rhsadd(inst->p##_##n##_Branch, v_tmp_);\\ }
#define _load_branch_dynamic_residual(p, n, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ ckt->rhsadd(inst->p##_##n##_Branch, v_tmp_);\\ }\\ }
#define _load_branch_whitenoise_residual(p, n, v) #define _load_branch_flickernoise_residual(p, n, v)
#define _load_branch_static(p, n, v)\\ { double v_tmp_ = M(v);\\ ckt->rhsadd(inst->p##_##n##_Branch, -v_tmp_*BR(p,n));\\ ckt->ldadd(inst->PTR_##p##_##n##_Branch_BB, -v_tmp_);\\ (inst->JDVAL_##p##_##n##_Branch_BB) = v_tmp_; }
#define _load_branch_dynamic(p, n, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ double req = v_tmp_*ckt->CKTag[0];\\ double veq = req*BR(p,n);\\ ckt->rhsadd(inst->p##_##n##_Branch, -veq);\\ ckt->ldadd(inst->PTR_##p##_##n##_Branch_BB, -req);\\ }\\ (inst->JDVAL_##p##_##n##_Branch_BB) = v_tmp_; }
#define _load_dynamic_residual1(p, v)\\ if (ckt->CKTchargeCompNeeded) {\\ ckt->rhsadd(inst->p##_Node, -M(v));\\ }
#define _load_dynamic_residual2(p, n, v)\\ if (ckt->CKTchargeCompNeeded) {\\ double v_tmp_r = M(v);\\ ckt->rhsadd(inst->p##_Node, -v_tmp_r);\\ ckt->rhsadd(inst->n##_Node, v_tmp_r);\\ }
#define _load_dynamic_residual1(p,v)\\ if (ckt->CKTchargeCompNeeded) {\\ *(ckt->CKTstate0 + inst->STATE_##p##_GND) = M(v);\\ if (ckt->CKTmode & MODEINITTRAN)\\ *(ckt->CKTstate1 + inst->STATE_##p##_GND) =\\ *(ckt->CKTstate0 + inst->STATE_##p##_GND);\\ ckt->integrate(inst->STATE_##p##_GND);\\ if (ckt->CKTmode & MODEINITTRAN)\\ *(ckt->CKTstate1 + inst->STATE_##p##_GND+1) =\\ *(ckt->CKTstate0 + inst->STATE_##p##_GND+1);\\ ckt->rhsadd(inst->p##_Node,\\ -(*(ckt->CKTstate0 + inst->STATE_##p##_GND+1)));\\ }
#define _load_dynamic_residual2(p, n, v)\\ if (ckt->CKTchargeCompNeeded) {\\ *(ckt->CKTstate0 + inst->STATE_##p##_##n) = M(v);\\ if (ckt->CKTmode & MODEINITTRAN)\\ *(ckt->CKTstate1 + inst->STATE_##p##_##n) =\\ *(ckt->CKTstate0 + inst->STATE_##p##_##n);\\ ckt->integrate(inst->STATE_##p##_##n);\\ if (ckt->CKTmode & MODEINITTRAN)\\ *(ckt->CKTstate1 + inst->STATE_##p##_##n+1) =\\ *(ckt->CKTstate0 + inst->STATE_##p##_##n+1);\\ ckt->rhsadd(inst->p##_Node,\\ -(*(ckt->CKTstate0 + inst->STATE_##p##_##n+1)));\\ ckt->rhsadd(inst->n##_Node,\\ *(ckt->CKTstate0 + inst->STATE_##p##_##n+1));\\ }
#define _load_dynamic_jacobian1(Sp, Pp, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ double geq = v_tmp_*ckt->CKTag[0];\\ double ceq = geq*NP(Pp);\\ ckt->rhsadd(inst->Sp##_Node, ceq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, geq);\\ }\\ (inst->JDVAL_##Sp##_##Pp) += v_tmp_; }
#define _load_dynamic_jacobian2s(Sp, Sn, Pp, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ double geq = v_tmp_*ckt->CKTag[0];\\ double ceq = geq*NP(Pp);\\ ckt->rhsadd(inst->Sp##_Node, ceq);\\ ckt->rhsadd(inst->Sn##_Node, -ceq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, geq);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pp, -geq);\\ }\\ (inst->JDVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JDVAL_##Sn##_##Pp) -= v_tmp_; }
#define _load_dynamic_jacobian2p(Sp, Pp, Pn, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ double geq = v_tmp_*ckt->CKTag[0];\\ double ceq = geq*BP(Pp,Pn);\\ ckt->rhsadd(inst->Sp##_Node, ceq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, geq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pn, -geq);\\ }\\ (inst->JDVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JDVAL_##Sp##_##Pn) -= v_tmp_; }
#define _load_dynamic_jacobian4(Sp, Sn, Pp, Pn, v)\\ { double v_tmp_ = M(v);\\ if (ckt->CKTchargeCompNeeded) {\\ double geq = v_tmp_*ckt->CKTag[0];\\ double ceq = geq*BP(Pp,Pn);\\ ckt->rhsadd(inst->Sp##_Node, ceq);\\ ckt->rhsadd(inst->Sn##_Node, -ceq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pp, geq);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pn, geq);\\ ckt->ldadd(inst->PTR_J_##Sp##_##Pn, -geq);\\ ckt->ldadd(inst->PTR_J_##Sn##_##Pp, -geq);\\ }\\ (inst->JDVAL_##Sp##_##Pp) += v_tmp_;\\ (inst->JDVAL_##Sn##_##Pn) += v_tmp_;\\ (inst->JDVAL_##Sp##_##Pn) -= v_tmp_;\\ (inst->JDVAL_##Sn##_##Pp) -= v_tmp_; }
namespace { inline double limit(sCKT *ckt, double newv, double oldv) {
if (!(ckt->CKTmode & MODEDC)) { double dv = newv - oldv; const double lim = 3.0; if (fabs(dv) > lim) { ckt->incNoncon(); newv = oldv + (dv > 0.0 ? lim : -lim); } }
return (newv); } }
